mapper.select:
SELECT DISTINCT A.J_KEY, A.J_ID, B.NAME, B.USER_ID, A.PRICE_PAY, A.PAY_TYPE, A.PAY_STATUS, A.IS_MOBILE, D.STATUS as DELIVER_STATUS, A.INDATE, A.DEPOSIT_USER, A.CARD_CODE, A.IS_OFFLINE, B.USER_KEY FROM T_ORDER A INNER JOIN T_USER B ON A.USER_KEY = B.USER_KEY INNER JOIN T_ORDER_GOODS C ON A.J_KEY = C.J_KEY LEFT OUTER JOIN T_ORDER_DELIVERY D ON A.J_KEY = D.J_KEY WHERE 1=1
AND TO_CHAR(A.INDATE, 'YYYY-MM-DD') > TO_DATE(#{startSearchDate}, 'YYYY-MM-DD')
AND TO_CHAR(A.INDATE, 'YYYY-MM-DD') <= TO_DATE(#{endSearchDate}, 'YYYY-MM-DD')
AND A.PAY_STATUS IN (0, 1, 2)
AND A.PAY_STATUS = #{payStatus}
AND C.TYPE = #{goodsType}
AND A.UNIQUE_TYPE_LIST NOT IN ('5,')
AND A.UNIQUE_TYPE_LIST LIKE '5,'||'%'
AND A.IS_OFFLINE = #{isOffline}
AND A.PAY_TYPE = #{payType}
AND A.IS_MOBILE = #{isMobile}
AND B.USER_ID = #{searchText}
AND B.NAME LIKE '%'||#{searchText}||'%'
AND A.J_ID = #{searchText}
AND C.G_NAME LIKE '%'||#{searchText}||'%'
AND C.EXTEND_DAY = -1
AND C.EXTEND_DAY > -1
ORDER BY A.J_KEY DESC OFFSET #{startNumber} ROWS FETCH NEXT #{listLimitNumber} ROWS ONLY

SELECT COUNT(J_KEY) FROM ( SELECT DISTINCT A.J_KEY FROM T_ORDER A INNER JOIN T_USER B ON A.USER_KEY = B.USER_KEY INNER JOIN T_ORDER_GOODS C ON A.J_KEY = C.J_KEY LEFT OUTER JOIN T_ORDER_DELIVERY D ON A.J_KEY = D.J_KEY WHERE 1=1
AND TO_CHAR(A.INDATE, 'YYYY-MM-DD') > TO_DATE(#{startSearchDate}, 'YYYY-MM-DD')
AND TO_CHAR(A.INDATE, 'YYYY-MM-DD') <= TO_DATE(#{endSearchDate}, 'YYYY-MM-DD') + 1
AND A.PAY_STATUS IN (0, 1, 2)
AND A.PAY_STATUS = #{payStatus}
AND C.TYPE = #{goodsType}
AND A.UNIQUE_TYPE_LIST NOT IN ('5,')
AND A.UNIQUE_TYPE_LIST LIKE '5,'||'%'
AND A.IS_OFFLINE = #{isOffline}
AND A.PAY_TYPE = #{payType}
AND A.IS_MOBILE = #{isMobile}
AND B.USER_ID = #{searchText}
AND B.NAME LIKE '%'||#{searchText}||'%'
AND A.J_ID = #{searchText}
AND C.G_NAME LIKE '%'||#{searchText}||'%'
AND C.EXTEND_DAY = -1
AND C.EXTEND_DAY > -1
)

SELECT DISTINCT A.J_KEY, A.J_ID, B.NAME, B.USER_ID, A.PRICE_PAY, A.PAY_TYPE, A.PAY_STATUS, A.IS_MOBILE, D.STATUS as DELIVER_STATUS, A.INDATE, A.DEPOSIT_USER, A.CARD_CODE, A.CANCEL_DATE, A.IS_OFFLINE, B.USER_KEY FROM T_ORDER A INNER JOIN T_USER B ON A.USER_KEY = B.USER_KEY INNER JOIN T_ORDER_GOODS C ON A.J_KEY = C.J_KEY LEFT OUTER JOIN T_ORDER_DELIVERY D ON A.J_KEY = D.J_KEY WHERE 1=1
AND TO_CHAR(A.INDATE, 'YYYY-MM-DD') > TO_DATE(#{startSearchDate}, 'YYYY-MM-DD')
AND TO_CHAR(A.INDATE, 'YYYY-MM-DD') <= TO_DATE(#{endSearchDate}, 'YYYY-MM-DD') + 1
AND TO_CHAR(A.CANCEL_DATE, 'YYYY-MM-DD') > TO_DATE(#{startCancelSearchDate}, 'YYYY-MM-DD')
AND TO_CHAR(A.CANCEL_DATE, 'YYYY-MM-DD') <= TO_DATE(#{endCancelSearchDate}, 'YYYY-MM-DD') + 1
AND A.PAY_STATUS IN (8, 9, 10)
AND A.PAY_STATUS = #{payStatus}
AND A.IS_OFFLINE = #{isOffline}
AND A.PAY_TYPE = #{payType}
AND A.IS_MOBILE = #{isMobile}
AND B.USER_ID = #{searchText}
AND B.NAME LIKE '%'||#{searchText}||'%'
AND A.J_ID = #{searchText}
AND C.G_NAME LIKE '%'||#{searchText}||'%'
ORDER BY A.J_KEY DESC OFFSET #{startNumber} ROWS FETCH NEXT #{listLimitNumber} ROWS ONLY

SELECT COUNT(J_KEY) FROM ( SELECT DISTINCT A.J_KEY FROM T_ORDER A INNER JOIN T_USER B ON A.USER_KEY = B.USER_KEY INNER JOIN T_ORDER_GOODS C ON A.J_KEY = C.J_KEY LEFT OUTER JOIN T_ORDER_DELIVERY D ON A.J_KEY = D.J_KEY WHERE 1=1
AND TO_CHAR(A.INDATE, 'YYYY-MM-DD') > TO_DATE(#{startSearchDate}, 'YYYY-MM-DD')
AND TO_CHAR(A.INDATE, 'YYYY-MM-DD') <= TO_DATE(#{endSearchDate}, 'YYYY-MM-DD') + 1
AND TO_CHAR(A.CANCEL_DATE, 'YYYY-MM-DD') > TO_DATE(#{startCancelSearchDate}, 'YYYY-MM-DD')
AND TO_CHAR(A.CANCEL_DATE, 'YYYY-MM-DD') <= TO_DATE(#{endCancelSearchDate}, 'YYYY-MM-DD') + 1
AND A.PAY_STATUS IN (8, 9, 10)
AND A.PAY_STATUS = #{payStatus}
AND A.IS_OFFLINE = #{isOffline}
AND A.PAY_TYPE = #{payType}
AND A.IS_MOBILE = #{isMobile}
AND B.USER_ID = #{searchText}
AND B.NAME LIKE '%'||#{searchText}||'%'
AND A.J_ID = #{searchText}
AND C.G_NAME LIKE '%'||#{searchText}||'%'
)

SELECT J_CURRI_KEY, TIME
FROM T_ORDER_LEC_CURRI
WHERE J_LEC_KEY = #{jLecKey}
AND CURRI_KEY = #{curriKey}

SELECT B.USER_ID, B.NAME, TO_CHAR(A.INDATE, 'YYYY-MM-DD hh24:mi:ss') as INDATE, A.J_ID, A.TYPE, A.POINT, A.DESC_TYPE, A.DESCRIPTION FROM T_POINT A INNER JOIN T_USER B ON A.USER_KEY = B.USER_KEY WHERE 1=1
AND B.USER_ID = #{searchText}
AND B.USER_NAME LIKE '%'||#{searchText}||'%'
AND A.J_ID = #{searchText}
ORDER BY A.POINT_KEY DESC OFFSET #{startNumber} ROWS FETCH NEXT #{listLimitNumber} ROWS ONLY

SELECT COUNT (A.POINT_KEY) FROM T_POINT A INNER JOIN T_USER B ON A.USER_KEY = B.USER_KEY WHERE 1=1
AND B.USER_ID = #{searchText}
AND B.USER_NAME LIKE '%'||#{searchText}||'%'
AND A.J_ID = #{searchText}

mapper.insert:

    SELECT T_CALCULATE_SEQ.nextval FROM DUAL

INSERT INTO T_CALCULATE
(
    CALCULATE_KEY, C_KEY, TEACHER_KEY, INDATE, TARGET_DATE, YEAR_MONTH
)
VALUES
(
    #{calculateKey}, #{cKey}, #{teacherKey}, TO_DATE(#{indate}, 'YYYY-mm-dd hh24:mi:ss'), #{targetDate}, #{yearMonth}
)


    SELECT T_ORDER_DELIVERY_SEQ.nextval FROM DUAL

INSERT INTO T_ORDER_DELIVERY
(
    J_DELIVERY_KEY, J_KEY, DELIVERY_MASTER_KEY, STATUS, DELIVERY_START_DATE, DELIVERY_END_DATE, DELIVERY_NO
)
VALUES
(
    #{jDeliveryKey}, #{jKey}, #{deliveryMasterKey}, #{status}, TO_DATE(#{deliveryStartDate}, 'YYYY-mm-dd hh24:mi:ss'), TO_DATE(#{deliveryEndDate}, 'YYYY-mm-dd hh24:mi:ss'), #{deliveryNo}
)

mapper.update:
UPDATE T_ORDER_LEC
STATUS = #{status}, LIMIT_DAY = #{limitDay},
WHERE J_LEC_KEY = #{jLecKey}

UPDATE T_ORDER
SET PAY_STATUS = #{payStatus}
WHERE J_KEY = #{jKey}

UPDATE T_ORDER
DELIVERY_NANE = #{deliveryNane}, DELIVERY_TELEPHONE = #{deliveryTelephone}, DELIVERY_TELEPHONE_MOBILE = #{deliveryTelephoneMobile}, DELIVERY_EMAIL = #{deliveryEmail}, DELIVERY_ZIPCODE = #{deliveryZipcode}, DELIVERY_ADDRESS = #{deliveryAddress}, DELIVERY_ADDRESS_ROAD = #{deliveryAddressRoad}, DELIVERY_ADDRESS_ADD = #{deliveryAddressAdd},
WHERE J_KEY = #{jKey}

UPDATE T_ORDER_DELIVERY
DELIVERY_MASTER_KEY = #{deliveryMasterKey}, STATUS = #{status}, DELIVERY_NO = #{deliveryNo},
WHERE J_DELIVERY_KEY = #{jDeliveryKey}
