mapper.select:
SELECT A.G_KEY, A.NAME as GOODS_NANE, A.INDATE, A.IS_SHOW, A.IS_SELL, A.IS_FREE, D.NAME as TEACHER_NAME, E.STATUS FROM T_GOODS A LEFT JOIN T_GOODS_TEACHER_LINK B ON A.G_KEY = B.G_KEY LEFT JOIN T_TEACHER C ON B.TEACHER_KEY = C.TEACHER_KEY LEFT JOIN T_USER D ON C.USER_KEY = D.USER_KEY LEFT JOIN T_LEC E ON A.G_KEY = E.G_KEY WHERE 1=1 AND A.TYPE = #{goodsTypeKey}
AND A.G_KEY = #{searchText}
AND A.NAME LIKE '%'||#{searchText}||'%'
ORDER BY A.G_KEY DESC OFFSET #{startNumber} ROWS FETCH NEXT #{listLimitNumber} ROWS ONLY

SELECT count(G_KEY) as CNT FROM T_GOODS WHERE 1=1 AND TYPE = #{goodsTypeKey} ORDER BY G_KEY DESC
AND G_KEY = #{searchText}
AND NAME LIKE '%'||#{searchText}||'%'

SELECT A.*, B.name as goods_name
FROM T_LINK_KEY A INNER JOIN T_GOODS B
ON A.res_key = B.g_key
WHERE A.req_key = #{reqKey}

	AND A.res_type = #{resType}

SELECT A.*, B.name as goods_name
FROM T_LINK_KEY A INNER JOIN T_EXAM_MASTER B
ON A.res_key = B.exam_key
WHERE A.req_key = #{reqKey}

	AND A.res_type = #{resType}

SELECT Z.* FROM (
	SELECT POS FROM T_LEC_CURRI WHERE LEC_KEY = #{lecKey}
	ORDER BY POS DESC
) Z
WHERE ROWNUM = 1

SELECT COUNT(EXAM_KEY) FROM T_EXAM_MASTER

	AND EXAM_KEY = #{searchText}


	AND A.NAME LIKE '%'||#{searchText}||'%'

SELECT * FROM T_GOODS
WHERE TYPE = #{goodsTypeKey}

	AND EXAM_KEY = #{searchText}


	AND A.NAME LIKE '%'||#{searchText}||'%'

ORDER BY G_KEY DESC
OFFSET #{startNumber} ROWS FETCH NEXT #{listLimitNumber} ROWS ONLY

SELECT COUNT (G_KEY) FROM T_GOODS
WHERE TYPE = #{goodsTypeKey}

	AND EXAM_KEY = #{searchText}


	AND A.NAME LIKE '%'||#{searchText}||'%'

mapper.insert:
INSERT INTO T_GOODS_PRICE_OPTION
(
	PRICE_KEY, G_KEY, KIND, CTG_KEY, NAME, PRICE, SELL_PRICE, POINT, EXTEND_PERCENT
)
VALUES
(
	T_GOODS_PRICE_OPTION_SEQ.nextval, #{gKey}, #{kind}, '', '', #{price}, #{sellPrice}, #{point}, #{extendPercent}
)

	SELECT T_GOODS_PRICE_OPTION_SEQ.NEXTVAL FROM DUAL

INSERT INTO T_CATEGORY_GOODS
(
	CTG_G_KEY, CTG_KEY, G_KEY, POS
)
VALUES
(
	T_CATEGORY_GOODS_SEQ.nextval, #{ctgKey}, #{gKey}, #{pos}
)

INSERT INTO T_GOODS_TEACHER_LINK
(
	G_TEACHER_KEY, G_KEY, IS_PUBLIC_SUBJECT, SUBJECT_CTG_KEY, TEACHER_KEY, CALCULATE_RATE
)
VALUES
(
	T_GOODS_TEACHER_LINK_SEQ.nextval, #{gKey}, #{isPublicSubject}, #{subjectCtgKey}, #{teacherKey}, #{calculateRate}
)

INSERT INTO T_LINK_KEY
(
	LINK_KEY, REQ_KEY, RES_KEY, REQ_TYPE, RES_TYPE, POS, VALUE_BIT
)
VALUES
(
	T_LINK_KEY_SEQ.nextval, #{reqKey}, #{resKey}, #{reqType}, #{resType}, 0, #{valueBit}
)

INSERT INTO T_BOOK
(
	BOOK_KEY, G_KEY, STATUS, WRITER, PUBLISH_DATE, PAGE_CNT, ISBN, IS_DELIVERY_FREE,
	IS_SET, CONTENT_LIST, SUBJECT_CTG_KEY, CLASS_GROUP_CTG_KEY, GOODS_ID, NEXT_G_KEY
)
VALUES
(
	T_BOOK_SEQ.nextval, #{gKey}, #{status}, #{status}, #{writer}, #{publishDate}, #{pageCnt}, #{isbn}, #{isDeliveryFree},
	#{isSet}, #{contentList}, #{subjectCtgKey}, #{classGroupCtgKey}, #{goodsId}, #{nextGKey}
)

mapper.update:
UPDATE T_GOODS
CP_KEY = #{cpKey}, NAME = #{name}, INDATE = #{indate}, SELLSTARTDATE = #{sellstartdate}, TYPE = #{type}, IS_SHOW = #{isShow}, IS_SELL = #{isSell}, IS_FREE = #{isFree}, IS_NODC = #{isNodc}, IMAGE_LIST = #{imageList}, IMAGE_VIEW = #{imageView}, EMPHASIS = #{emphasis}, TAGS = #{tags}, SUMMARY = #{summary}, DESCRIPTION = #{description}, CALCULATE_RATE = #{calculateRate}, IS_FREEBIE_DELIVERY_FREE = #{isFreebieDeliveryFree}, IS_QUICK_DELIVERY = #{isQuickDelivery}, GOODS_ID = #{goodsId},
WHERE G_KEY = #{gKey}

UPDATE T_GOODS_PRICE_OPTION
G_KEY = #{gKey}, KIND = #{kind}, PRICE = #{price}, SELL_PRICE = #{sellPrice}, POINT = #{point}, EXTEND_PERCENT = #{extendPercent},
WHERE PRICE_KEY = #{priceKey}

UPDATE T_LEC
G_KEY = #{gKey}, TEACHER_KEY = #{teacherKey}, CLASS_GROUP_CTG_KEY = #{classGroupCtgKey}, SUBJECT_CTG_KEY = #{subjectCtgKey}, STEP_CTG_KEY = #{stepCtgKey}, STATUS = #{status}, REGDATE = #{regdate}, STARTDATE = #{startdate}, LIMIT_DAY = #{limitDay}, LIMIT_COUNT = #{limitCount}, LEC_TIME = #{lecTime}, MULTIPLE = #{multiple}, LEC_DATE_YEAR = #{lecDateYear}, LEC_DATE_MONTH = #{lecDateMonth}, EXAM_YEAR = #{examYear}, IS_PACK = #{isPack}, GOODS_ID = #{goodsId},
WHERE LEC_KEY = #{lecKey}

UPDATE T_GOODS_TEACHER_LINK
IS_PUBLIC_SUBJECT = #{isPublicSubject}, SUBJECT_CTG_KEY = #{subjectCtgKey}, TEACHER_KEY = #{teacherKey}, CALCULATE_RATE = #{calculateRate},
WHERE G_TEACHER_KEY = #{gTeacherKey}

UPDATE T_LINK_KEY
REQ_KEY = #{reqKey}, RES_KEY = #{resKey}, RES_TYPE = #{reqType}, RES_TYPE = #{resType}, VALUE_BIT = #{valueBit},
WHERE LINK_KEY = #{linkKey}

UPDATE T_LEC_CURRI
NAME = #{name}, VOD_FILE_LOW = #{vodFileLow}, VOD_FILE_HIGH = #{vodFileHigh}, VOD_FILE_MOBILE_LOW = #{vodFileMobileLow}, VOD_FILE_MOBILE_HIGH = #{vodFileMobileHigh}, VOD_TIME = #{vodTime}, DATA_FILE = #{dataFile}, IS_SHOW = #{isShow}, IS_SAMPLE = #{isSample}, POS = #{pos},
WHERE CURRI_KEY = #{curriKey}

UPDATE T_BOOK
STATUS = #{status}, WRITER = #{writer}, PUBLISH_DATE = #{publishDate}, PAGE_CNT = #{pageCnt}, ISBN = #{isbn}, IS_DELIVERY_FREE = #{isDeliveryFree}, IS_SET = #{isSet}, CONTENT_LIST = #{contentList}, SUBJECT_CTG_KEY = #{subjectCtgKey}, CLASS_GROUP_CTG_KEY = #{classGroupCtgKey}, GOODS_ID = #{goodsId}, NEXT_G_KEY = #{nextGKey},
WHERE BOOK_KEY = #{bookKey}

UPDATE T_EXAM_MASTER
NAME = #{name}, INDATE = #{indate}, CLASS_CTG_KEY = #{classCtgKey}, SUBJECT_CTG_KEY = #{subjectCtgKey}, SELECT_SUBJECT_COUNT = #{selectSubjectCount}, ACCEPT_START_DATE = #{acceptStartDate}, ACCEPT_END_DATE = #{acceptEndDate}, ONLINE_START_DATE = #{onlineStartDate}, ONLINE_END_DATE = #{onlineEndDate}, ONLINE_TIME = #{onlineTime}, OFFLINE_DATE = #{offlineDate}, OFFLINE_TIME_PERIOD = #{offlineTimePeriod}, OFFLINE_TIME_PLACE = #{offlineTimePlace}, ANSWER_COUNT = #{answerCount}, QUESTION_COUNT = #{questionCount}, PRINT_QUESTION_FILE = #{printQuestionFile}, PRINT_COMMENTARY_FILE = #{printCommentaryFile}, IS_SHOW_STATIC_TOTAL_RANK = #{isShowStaticTotalRank}, IS_SHOW_STATIC_TOTAL_AVG = #{isShowStaticTotalAvg}, IS_SHOW_FILES = #{isShowFiles}, CLASS_GROUP_CTG_KEY = #{classGroupCtgKey}, EXAM_YEAR = #{examYear}, UNIT_CTG_KEY = #{unitCtgKey}, DIVISION_CTG_KEY = #{divisionCtgKey}, REF_KEY = #{refKey}, IS_REAL_FREE = #{isRealFree}, IS_GICHUL = #{isGichul},
WHERE EXAM_KEY = #{examKey}
