mapper.select:
SELECT TO_CHAR(INDATE, 'YYYY-MM') as DAY, SUM(PRICE_PAY) as PRICE FROM ( SELECT distinct A.J_KEY, A.PRICE_PAY, A.INDATE, B.TYPE, A.PAY_STATUS, A.PAY_TYPE FROM T_ORDER A INNER JOIN T_ORDER_GOODS B ON A.J_KEY = B.J_KEY WHERE 1=1 AND A.PAY_STATUS = 2 AND A.PAY_TYPE != 20
AND B.TYPE = #{goodsType}
) WHERE TO_CHAR(INDATE, 'YYYY') = #{year} GROUP BY TO_CHAR(INDATE, 'YYYY-MM') ORDER BY DAY ASC

SELECT TO_CHAR(INDATE, 'YYYY') as DAY, SUM(PRICE_PAY) as PRICE FROM ( SELECT distinct A.J_KEY, A.PRICE_PAY, A.INDATE, B.TYPE, A.PAY_STATUS, A.PAY_TYPE FROM T_ORDER A INNER JOIN T_ORDER_GOODS B ON A.J_KEY = B.J_KEY WHERE 1=1 AND A.PAY_STATUS = 2 AND A.PAY_TYPE != 20
AND B.TYPE = #{goodsType}
) GROUP BY TO_CHAR(INDATE, 'YYYY') ORDER BY DAY ASC

SELECT TO_CHAR(INDATE, 'YYYY-MM-DD') as DAY, SUM(PRICE_PAY) as PRICE FROM ( SELECT distinct A.J_KEY, A.PRICE_PAY, A.INDATE, B.TYPE, A.PAY_STATUS, A.PAY_TYPE FROM T_ORDER A INNER JOIN T_ORDER_GOODS B ON A.J_KEY = B.J_KEY WHERE 1=1 AND A.PAY_STATUS = 2 AND A.PAY_TYPE != 20
AND B.TYPE = #{goodsType}
) WHERE TO_CHAR(INDATE, 'YYYY-MM') = #{month} GROUP BY TO_CHAR(INDATE, 'YYYY-MM-DD') ORDER BY DAY ASC

SELECT TO_CHAR(INDATE, 'YYYY-MM') as DAY, SUM(PRICE_PAY) as PRICE FROM ( SELECT distinct A.J_KEY, A.PRICE_PAY, A.INDATE, B.TYPE, A.PAY_STATUS, A.PAY_TYPE, B.PM_TYPE FROM T_ORDER A INNER JOIN T_ORDER_GOODS B ON A.J_KEY = B.J_KEY WHERE 1=1 AND B.TYPE = 5
AND B.PM_TYPE = #{pmType}
AND A.PAY_STATUS = 2 AND A.PAY_TYPE != 20 ) WHERE TO_CHAR(INDATE, 'YYYY') = #{year} GROUP BY TO_CHAR(INDATE, 'YYYY-MM') ORDER BY DAY ASC

SELECT TO_CHAR(INDATE, 'YYYY') as DAY, SUM(PRICE_PAY) as PRICE FROM ( SELECT distinct A.J_KEY, A.PRICE_PAY, A.INDATE, B.TYPE, A.PAY_STATUS, A.PAY_TYPE, B.PM_TYPE FROM T_ORDER A INNER JOIN T_ORDER_GOODS B ON A.J_KEY = B.J_KEY WHERE 1=1 AND B.TYPE = 5
AND B.PM_TYPE = #{pmType}
AND A.PAY_STATUS = 2 AND A.PAY_TYPE != 20 ) GROUP BY TO_CHAR(INDATE, 'YYYY') ORDER BY DAY ASC

SELECT TO_CHAR(INDATE, 'YYYY-MM-DD') as DAY, SUM(PRICE_PAY) as PRICE FROM ( SELECT distinct A.J_KEY, A.PRICE_PAY, A.INDATE, B.TYPE, A.PAY_STATUS, A.PAY_TYPE, B.PM_TYPE FROM T_ORDER A INNER JOIN T_ORDER_GOODS B ON A.J_KEY = B.J_KEY WHERE 1=1 AND B.TYPE = 5
AND B.PM_TYPE = #{pmType}
AND A.PAY_STATUS = 2 AND A.PAY_TYPE != 20 ) WHERE TO_CHAR(INDATE, 'YYYY-MM') = #{month} GROUP BY TO_CHAR(INDATE, 'YYYY-MM-DD') ORDER BY DAY ASC

SELECT * FROM T_CALCULATE_OPTION
WHERE TEACHER_KEY = #{teacherKey}
AND TO_CHAR(TARGET_DATE, 'YYYYMM') = #{targetDate}
ORDER BY CALCULATE_OPTION_KEY DESC

mapper.insert:

    SELECT T_CALCULATE_OPTION_SEQ.nextval FROM DUAL

INSERT INTO T_CALCULATE_OPTION
(
  CALCULATE_OPTION_KEY, TEACHER_KEY, TARGET_DATE, TITLE, PRICE
)
VALUES
(
  #{calculateOptionKey}, #{teacherKey}, TO_DATE(#{targetDate}, 'YYYY-MM-DD hh24:mi:ss'), #{title}, #{price}
)

mapper.update:
UPDATE T_CALCULATE_OPTION
TITLE = #{title}, TARGET_DATE = TO_DATE(#{targetDate}, 'YYYY-MM-DD hh24:mi:ss'), PRICE = #{price},
WHERE CALCULATE_OPTION_KEY=#{calculateKey}
